import { useLocation } from "react-router-dom";
import { useState } from "react";
import Image from "react-bootstrap/Image";

function Header() {
  const location = useLocation();
  const [isOpen, setIsOpen] = useState(false);

  const isCoursesPageActive = () => {
    const { pathname } = location;
    return (
      pathname === "/courses" ||
      pathname.startsWith("/course/") ||
      pathname.startsWith("/exam-availability/") ||
      pathname.startsWith("/lecture/")
    );
  };

  return (
    <div className="bg-white border-b border-gray-200">
      <div className="container mx-auto flex justify-between items-center p-4">
        <a href="/">
          <Image src={"./assets/images/logo.svg"} alt="Logo" className="w-40" />
        </a>
        <button
          onClick={() => setIsOpen(!isOpen)}
          className="lg:hidden p-2 focus:outline-none"
          aria-label="Toggle navigation"
        >
          {/* Hamburger icon */}
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16m-7 6h7" />
          </svg>
        </button>
        <nav className={`lg:flex lg:items-center ${isOpen ? "block" : "hidden"} flex-col lg:flex-row`}>
          <a href="/" className={`p-4 ${location.pathname === "/" ? "font-bold text-blue-600" : ""}`}>
            Home
          </a>
          <a href="/courses" className={`p-4 ${isCoursesPageActive() ? "font-bold text-blue-600" : ""}`}>
            Courses
          </a>
          <a href="https://www.urduitacademy.com/quiz/" className={`p-4 ${location.pathname === "/aboutus" ? "font-bold text-blue-600" : ""}`} target="_blank" rel="noopener noreferrer">
            Quiz
          </a>
          <a href="/aboutus" className={`p-4 ${location.pathname === "/aboutus" ? "font-bold text-blue-600" : ""}`}>
            About
          </a>
          <a href="/help" className={`p-4 ${location.pathname === "/help" ? "font-bold text-blue-600" : ""}`}>
            Help
          </a>
          <a href="/blogs" className={`p-4 ${location.pathname === "/blogs" ? "font-bold text-blue-600" : ""}`}>
            Blog
          </a>
          <form className="flex items-center lg:ml-4">
            <input
              type="search"
              placeholder="Search"
              className="border border-gray-300 rounded-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              aria-label="Search"
            />
          </form>
        </nav>
      </div>
    </div>
  );
}

export default Header;
